import axios from "axios";
import { useEffect, useState } from "react";
import { Link } from "react-router-dom";

export default function ListTask() {

    const [tasks, setTasks] = useState([]);
    useEffect(() => {
        getTasks();
    }, []);

    function getTasks() {
        axios.get('http://localhost:8888/api/tasks/').then(function(response) {
            console.log(response.data);
            setTasks(response.data);
        });
    }

    const deleteTask = (id) => {
        axios.delete(`http://localhost:8888/api/task/${id}/delete`).then(function(response){
            console.log(response.data);
            getTasks();
        });
    }

    return (
        <div>
            <h1>Task List</h1>
            <table>
                <thead>
                    <tr>
                        <th>#</th>
                        <th>Name</th>
                        <th>Time</th>
                        <th>Date</th>
                    </tr>
                </thead>
                <tbody>
                    {tasks.map((task, key) =>
                        <tr key={key}>
                            <td>{task.id}</td>
                            <td>{task.name}</td>
                            <td>{task.time}</td>
                            <td>{task.date}</td>
                        </tr>
                    )}

                </tbody>
            </table>
        </div>
    )
}
