import { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate, useParams } from "react-router-dom";

export default function EditTask() {
    const navigate = useNavigate();
    const { id } = useParams();

    const [task, setTask] = useState({
        name: "",
        time: "",
        date: ""
    });

    useEffect(() => {
        getTask();
    }, []);

    function getTask() {
        axios.get(`http://localhost/api/task/${id}`).then(function(response) {
            console.log(response.data);
            setTask(response.data);
        });
    }

    const handleChange = (event) => {
        const { name, value } = event.target;
        setTask(prevTask => ({...prevTask, [name]: value}));
    };

    const handleSubmit = (event) => {
        event.preventDefault();

        axios.put(`http://localhost:8888/api/task/${id}/edit`, task)
            .then(function(response){
                console.log(response.data);
                navigate('/');
            })
            .catch(function(error) {
                console.error("Error editing task: ", error);
            });
    };

    return (
        <div>
            <h1>Edit Task</h1>
            <form onSubmit={handleSubmit}>
                <table cellSpacing="10">
                    <tbody>
                        <tr>
                            <th>
                                <label>Name: </label>
                            </th>
                            <td>
                                <input value={task.name} type="text" name="name" onChange={handleChange} />
                            </td>
                        </tr>
                        <tr>
                            <th>
                                <label>Time: </label>
                            </th>
                            <td> 
                                <input value={task.time} type="time" name="time" onChange={handleChange} />
                            </td>
                        </tr>
                        <tr>
                            <th>
                                <label>Date: </label>
                            </th>
                            <td>
                                <input value={task.date} type="date" name="date" onChange={handleChange} />
                            </td>
                        </tr>
                        <tr>
                            <td colSpan="2" align ="right">
                                <button>Save</button>
                            </td>
                        </tr>
                    </tbody>
                </table>
            </form>
        </div>
    );
}
